mapper.select:
select user_name
  from modeling.users
 where user_id = #{userId}

mapper.insert:
        insert into modeling.users(user_name
                                  , email
                                  , password
                                  , phone
                                  , gender_id
                                  , job_id
                                  , birth_date
--                                   , is_ad_receive
        )
        VALUES (#{userName}
               ,#{userEmail}
               , #{userPwd}
               , #{userPhone}
               , #{userGenderId}
               , #{userJobId}
               , #{userBirthDate}
--                , #{userIsAdReceive}
               )

mapper.select:
select count(email)
from modeling.users
where email=#{userEmail}

insert into modeling.email_verification(email
                          , token
                          , expire_at
                          , verified)
VALUES (#{email}
        , #{token}
        , #{expireAt}
        , #{verified})
